        mileage,
        condition,
        features,
        transmission,
        priceRange,
        contactNumber,
    }

    const newErrors = {};
    if (carMake.trim() === '') {
      newErrors.carMake = 'Car Make is required';
    }
    if (carModel.trim() === '') {
      newErrors.carModel = 'Car Model is required';
    }
    if (year === '') {
      newErrors.year = 'Year is required';
    }
    if (mileage <= 0) {
      newErrors.mileage = 'Mileage must be greater than 0';
    }
    if (condition === '') {
      newErrors.condition = 'Condition is required';
    }
    if (features.length === 0) {
      newErrors.features = 'At least one feature must be selected';
    }
    if (transmission === '') {
      newErrors.transmission = 'Transmission is required';
    }
    if (priceRange <= 0) {
      newErrors.priceRange = 'Price Range must be greater than 0';
    }
    if (contactNumber.trim() === '') {
      newErrors.contactNumber = 'Contact Number is required';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      console.log(formData);
      setCarMake("");
      setCarModel("")
      setCondition("");
      setContactNumber("")
      setFeatures([])
      setMileage(0)
      setTransimission("")
      setYear('')
      setPriceRange(0)
      setErrors({});
      // Perform further actions with the form data
    }


   }


