import React, { useState } from 'react'

function Form() {
    const [formData, setFormData] =  useState({
        carMake: "",
        carModel:"",
        year: "",
        mileage: "",
        condition:"",
        features: [],
        priceRange:0,
        contactNumber: ''
    })

    const handleChange = (e) => {
        setFormData(e.target.value)
    }
  return (
    <div>
      <form>
        <label>Car Make</label>
        <input type='text' value={formData.carMake} onChange={handleChange} />
      </form>
    </div>
  )
}

export default Form
