import React, { useState } from 'react'

function Form() {
    const [carMake, setCarMake] = useState('')
    const [carModel, setCarModel] = useState('')
    const [year, setYear] = useState('')
    const [mileage, setMileage] = useState(0)
    const [condition, setCondition] = useState('')
    const [features, setFeatures] = useState([])
    const [transmission, setTransimission] = useState('')
    const [priceRange, setPriceRange] = useState(0)
    const [contactNumber, setContactNumber] = useState('')
    const [errors, setErrors] = useState({});


   const handleSubmit = (e) => {
    e.preventDefault()
    const formData =  {
        carMake,
        carModel,
        year,
        mileage,
        condition,
        features,
        transmission,
        priceRange,
        contactNumber,
    }
    console.log(formData)
    setCarMake("");
    setCarModel("")
    setCondition("");
    setContactNumber("")
    setFeatures([])
    setMileage(0)
    setTransimission("")
    setYear('')
    setPriceRange(0)
   }


  return (
    <form onSubmit={handleSubmit}>
        <div>
            <label>Car Make: </label>
            {/** Handling the text inputs*/}
            <input
            type='text'
            value={carMake}
            onChange={(e) => setCarMake(e.target.value)}
            />
        </div>
        <div>
            <label>Car Model: </label>
            <input
            type='text'
            value={carModel}
            onChange={(e) => setCarModel(e.target.value)}
            />
        </div>
        <div>
            <label>Year : </label>
            {/**Handling the years and the dates */}
            <input type='date' value={year} onChange={(e) => setYear(e.target.value)} />
        </div>
        <div>
            <label>Mileage : </label>
            {/** Handling the number inputs */}
            <input type='number' value={mileage} onChange={(e) => setMileage(parseInt(e.target.value))} />
        </div>
        <div>
            <label>Condition:</label>
            <div>
                <label>
                    <input type='radio' 
                    value='Excellent' 
                    checked={condition === 'Excellent'}
                    onChange={(e) => setCondition(e.target.value)} />
                    Excellent
                </label>
                <label>
                    <input type='radio' 
                    value='Good' 
                    checked={condition === 'Good'}
                    onChange={(e) => setCondition(e.target.value)} />
                    Good
                </label>
                <label>
                    <input type='radio' 
                    value='Fair' 
                    checked={condition === 'Fair'}
                    onChange={(e) => setCondition(e.target.value)} />
                    Fair
                </label>
                <label>
                    <input type='radio' 
                    value='Poor' 
                    checked={condition === 'Poor'}
                    onChange={(e) => setCondition(e.target.value)} />
                    Fair
                </label>
            </div>
        </div>
        <div>
            <label>Features: </label>
            <label>
            <input 
            type='checkbox'
            value='Air conditioning'
            checked={features.includes('Air conditioning')}
            onChange={(e) => {
                if(e.target.checked){
                    setFeatures([...features, e.target.value])
                } else {
                    setFeatures(features.filter((feature) => feature !== e.target.value))
                }
            }} />Air conditioning
            </label>
            <label>
            <input 
            type='checkbox'
            value='Power Steering'
            checked={features.includes('Power Steering')}
            onChange={(e) => {
                if(e.target.checked){
                    setFeatures([...features, e.target.value])
                } else {
                    setFeatures(features.filter((feature) => feature !== e.target.value))
                }
            }} />Power Steering
            </label>
            <label>
            <input 
            type='checkbox'
            value='Power Windows'
            checked={features.includes('Power Windows')}
            onChange={(e) => {
                if(e.target.checked){
                    setFeatures([...features, e.target.value])
                } else {
                    setFeatures(features.filter((feature) => feature !== e.target.value))
                }
            }} />Power Windows
            </label>
            <label>
            <input 
            type='checkbox'
            value='ABS'
            checked={features.includes('ABS')}
            onChange={(e) => {
                if(e.target.checked){
                    setFeatures([...features, e.target.value])
                } else {
                    setFeatures(features.filter((feature) => feature !== e.target.value))
                }
            }} />ABS
            </label>
            <label>
            <input 
            type='checkbox'
            value='Navigation System'
            checked={features.includes('Navigation System')}
            onChange={(e) => {
                if(e.target.checked){
                    setFeatures([...features, e.target.value])
                } else {
                    setFeatures(features.filter((feature) => feature !== e.target.value))
                }
            }} />Navigation System
            </label>
        </div>
        <div>
            <label>Transmission: </label>
            <select 
            value={transmission}
            onChange={(e) => setTransimission(e.target.value)}
                >
                <option value="">Set Transmission</option>
                <option value="Automatic">Automatic</option>
                <option value="Manual">Manual</option>
            </select>
        </div>
        <div>
            <label>Price range: </label>
            <input 
            type='range'
            min='0' 
            max= '100'
            value={priceRange}
            onChange={(e) => setPriceRange(parseInt(e.target.value)) }/>
        </div>
        <div>
            <label>Contact Number : </label>
            <input 
            type="text"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}/>
        </div>
        <button type="submit">Submit</button>
    </form>
  )
}

export default Form
